import React, { Component } from 'react'
import { useNavigate } from 'react-router-dom';

class LoginComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            username: 'username',
            password: '',
            isSuccess: false,
            isLoginFailed: false
        }

        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)



    }

    render() {
        return (

            <div className="container">
                <form>
  <div className="mb-3">
    <label for="exampleInputEmail1" className="form-label">Email address</label>
    <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"/>
    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
  </div>
  <div class="mb-3">
    <label for="exampleInputPassword1" class="form-label">Password</label>
    <input type="password" class="form-control" id="exampleInputPassword1"/>
  </div>
  <div class="mb-3 form-check">
    <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
    <label class="form-check-label" for="exampleCheck1">Check me out</label>
  </div>
  <button type="submit" class="btn btn-primary">Submit</button>
</form>
                <h1>Login</h1>
                <div className="container">
                    {this.state.isLoginFailed && <div className="alert alert-warning">Invalid Credentials</div>}
                    {this.state.isSuccess && <div>Login Successful</div> }
                    <label>User Name:</label> 
                    <input type="text" name="username" value={this.state.username} onChange={this.handleChange}/>
                    <br />
                    <lable>Password:</lable><input type="password" name="password" value={this.state.password} onChange={this.handleChange}/>
                    <br />
                    <button className="btn btn-success" onClick={this.loginClicked}>Login</button>
                </div>
            </div>
        )
    }

    handleChange(event) {
        this.setState(
            {
                [event.target.name]
                : event.target.value
            }
        )
    }

    loginClicked() {
        // gaby, hello123
        if(this.state.username==='gaby' && this.state.password==='hello123') {
            this.setState({ isSuccess: true })
            this.setState({ isLoginFailed: false })
            this.props.navigate(`/welcome/${this.state.username}`);
        } else {
            this.setState({ isSuccess: false })
            this.setState({ isLoginFailed: true })
        }
    }

}

function LoginComponentWithNavigate(props) {
    const navigate = useNavigate();
    return <LoginComponent {...props} navigate={navigate} />
}

export default LoginComponentWithNavigate;
