import React, { Component } from 'react'
import { useNavigate } from 'react-router-dom';

class LoginComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            username: 'username',
            password: '',
            isSuccess: false,
            isLoginFailed: false
        }

        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)



    }

    render() {
        return (
            <div>
                            <form>
                <h3>Sign In</h3>

                <div className="form-group">
                    <label>Email address</label>
                    <input type="email" className="form-control" placeholder="Enter email" />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password" />
                </div>

                <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input type="checkbox" className="custom-control-input" id="customCheck1" />
                        <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                    </div>
                </div>

                <button type="submit" className="btn btn-primary btn-block">Submit</button>
                <p className="forgot-password text-right">
                    Forgot <a href="#">password?</a>
                </p>
            </form>
                <h1>Login</h1>
                <div className="container">
                    {this.state.isLoginFailed && <div className="alert alert-warning">Invalid Credentials</div>}
                    {this.state.isSuccess && <div>Login Successful</div> }
                    <label>User Name:</label> 
                    <input type="text" name="username" value={this.state.username} onChange={this.handleChange}/>
                    <br />
                    <lable>Password:</lable><input type="password" name="password" value={this.state.password} onChange={this.handleChange}/>
                    <br />
                    <button className="btn btn-success" onClick={this.loginClicked}>Login</button>
                </div>
            </div>
        )
    }

    handleChange(event) {
        this.setState(
            {
                [event.target.name]
                : event.target.value
            }
        )
    }

    loginClicked() {
        // gaby, hello123
        if(this.state.username==='gaby' && this.state.password==='hello123') {
            this.setState({ isSuccess: true })
            this.setState({ isLoginFailed: false })
            this.props.navigate(`/welcome/${this.state.username}`);
        } else {
            this.setState({ isSuccess: false })
            this.setState({ isLoginFailed: true })
        }
    }

}

function LoginComponentWithNavigate(props) {
    const navigate = useNavigate();
    return <LoginComponent {...props} navigate={navigate} />
}

export default LoginComponentWithNavigate;
